mapper.select:
SELECT
        COUNT(item_group.item_group_id)
FROM (
        SELECT
                DISTINCT item_group.item_group_id
        FROM    item_group

        LEFT JOIN    item_group_tag USING (item_group_id)
        WHERE   tag_id IN (
            #{item}
        )

) item_group
JOIN (
        SELECT  item_group_id, price price_per_day
        FROM    item
        JOIN (
                SELECT
                        item_id,
                        MIN(start_date) start_date,
                        MAX(end_date) end_date,
                        AVG(price) price
                FROM    item_schedule
                GROUP BY item_id
        ) USING (item_id)
        WHERE   start_date >= #{startDate}
        AND   end_date <= #{endDate}
        AND   price >= #{overPrice}
         AND price <= #{underPrice} 
        GROUP BY item_group_id
) USING (item_group_id)
