mapper.select:
SELECT  *
FROM    question
WHERE   type = 'CUSTOMER'
AND   item_group_id = #{itemGroupId}
ORDER BY created_at DESC
LIMIT (#{currentPage} - 1) * #{pageSize}, #{pageSize}

SELECT  *
FROM    question
WHERE   type = 'SELLER'
  AND   item_group_id IN (
        SELLER  item_group_id
        FROM    item_group
        WHERE   seller_id = #{sellerId}
)
ORDER BY created_at DESC
LIMIT (#{currentPage} - 1) * #{pageSize}, #{pageSize}

SELECT  *
FROM    question
WHERE   member_id = #{memberId}
ORDER BY created_at DESC
LIMIT   (#{currentPage} - 1) * #{pageSize}, #{pageSize}

SELECT  COUNT(*)
FROM    question
WHERE   member_id = #{memberId}
